mapper.update:
UPDATE MEMBER
SET NAME = #{name}, EMAIL = #{email}, PASSWORD = #{password}, PNUMBER = #{pnumber}
WHERE ID = #{id}

mapper.select:
SELECT NAME, EMAIL, ID, PNUMBER
FROM MEMBER
WHERE ID = #{id}

SELECT NAME, ID, PASSWORD, ROLE
FROM MEMBER
WHERE ID = #{id}

SELECT PASSWORD
FROM MEMBER
WHERE NAME = #{name} AND ID = #{id} AND EMAIL = #{email}
